<?php/* * call to action section */?>	<section id="call-to-action" class="call-to-action-section section-bg-wrapper default-section">		<div class="section-bg">			<div class="container">				<div class="section-content">					<div class="section-table-wrapper">						<?php						$mp_entrepreneur_call_to_action_title        = esc_html( get_theme_mod( mp_entrepreneur_get_prefix() . 'call_to_action_title' ) );						$mp_entrepreneur_call_to_action_button_url   = esc_url( get_theme_mod( mp_entrepreneur_get_prefix() . 'call_to_action_button_url', '#first-section' ) );						$mp_entrepreneur_call_to_action_button_label = esc_html( get_theme_mod( mp_entrepreneur_get_prefix() . 'call_to_action_button_label', __( 'book your visit', 'entrepreneur-lite' ) ) );						?>						<div class="section-header">							<?php							if ( get_theme_mod( mp_entrepreneur_get_prefix() . 'call_to_action_title', false ) === false ) :								?>								<h2><?php _e( 'Book your visit online and save 10&#37;', 'entrepreneur-lite' ); ?></h2>								<?php							else:								if ( ! empty( $mp_entrepreneur_call_to_action_title ) ):									?>									<h2><?php echo $mp_entrepreneur_call_to_action_title; ?></h2>									<?php								endif;							endif;							?>						</div>						<div class="section-buttons">							<?php							if ( ! empty( $mp_entrepreneur_call_to_action_button_label ) && ! empty( $mp_entrepreneur_call_to_action_button_url ) ):								?>								<a href="<?php echo $mp_entrepreneur_call_to_action_button_url; ?>"								   title="<?php echo $mp_entrepreneur_call_to_action_button_label; ?>"								   class="button button-size-large"><?php echo $mp_entrepreneur_call_to_action_button_label; ?></a>								<?php							endif;							?>						</div>					</div>				</div>			</div>		</div>	</section><?php